create table t_role_user (id BIGINT(20) NOT NULL AUTO_INCREMENT COMMENT '唯一标识',role_id BIGINT(20) NOT NULL COMMENT '角色ID',user_id VARCHAR(20) NOT NULL COMMENT '用户ID',create_time DATETIME NOT NULL COMMENT '创建时间',update_time DATETIME NOT NULL COMMENT '更新时间',create_user VARCHAR(50) NOT NULL COMMENT '创建用户',update_user VARCHAR(50) NOT NULL COMMENT '更新用户',ts TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP  COMMENT '默认时间',is_delete TINYINT(1) NOT NULL DEFAULT 0  COMMENT '刪除标志',version TINYINT(4) DEFAULT 0  COMMENT '版本号',version TINYINT DEFAULT 0  COMMENT '版本号', PRIMARY KEY (id)) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='角色-用户关系';
